import './App.css';
import image from './image/1.jpg'
import image1 from './image/logo.png'
import image2 from './image/back.jpg'
function App() {
  return (
    <div className="App">
      <div className="container">
        <div id="image">
          <img className="img-fluid" src={image} />
        </div>
        <form id="form" method="POST" action="" >
          <img className="logo" src={image1} />
          <input placeholder="نام کاربری" dir="ltr" className="text-center"/>
          <input placeholder="گذر واژه" dir="ltr" className="text-center"/>
          <p></p>
          <button className="btn" type="submit"><span>ورود</span></button>
        </form>
      </div>
    </div>
  );
}

export default App;
