import React from 'react'
import '../App.css'
import image1 from '../image/logo.png'
const login = (props) => {
     return(
        <form id="form" method="POST" action="" >
        <img className="logo" src={image1} />
        <input placeholder="پست الکترونیک" dir="ltr" className="text-center"/>
        <input placeholder="گذر واژه" dir="ltr" className="text-center"/>
        <button className="btn" type="submit"><span>ورود</span></button>
        <p className="text-center">.اگر ثبت نام نکرده اید <a onClick={add} href="" style={{color:'#00bcef' , textDecoration:'none'}}>اینجا</a> کلیک کنید</p>
      </form>
     );
}
export default login;
